    private void btnSalirMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_btnSalirMouseClicked
        // TODO add your handling code here:
        try {
            int opcion = JOptionPane.showConfirmDialog(rootPane, "¿Estás seguro de salir de la aplicación?");
            //Si selecciona Yes, se sale de la aplicación y muestra el mensaje en consola
            if (opcion == JOptionPane.YES_OPTION) {
                FrmPanelLogin.this.dispose();
                System.out.println("Se ha cerrado la sesión");
            } 
        } catch (HeadlessException e) {
            JOptionPane.showMessageDialog(rootPane, e);
        }
    }//GEN-LAST:event_btnSalirMouseClicked

